import{_ as a,o as s,c as e,V as o}from"./chunks/framework.364d6ed5.js";const m=JSON.parse('{"title":"Installing Forme CodeGen","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/getting-started/installing-codegen.md","filePath":"documentation/getting-started/installing-codegen.md","lastUpdated":1684568320000}'),n={name:"documentation/getting-started/installing-codegen.md"},l=o('<h1 id="installing-forme-codegen" tabindex="-1">Installing Forme CodeGen <a class="header-anchor" href="#installing-forme-codegen" aria-label="Permalink to &quot;Installing Forme CodeGen&quot;">​</a></h1><p>Forme includes a full featured code generation cli tool, which will speed up your development workflow by scaffolding out classes for you. To install it, run:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">composer</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">global</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">require</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">forme/codegen</span></span></code></pre></div><p>It should then be available for you globally on your command line. Try running:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">forme</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-V</span></span></code></pre></div><p>And you should get an output like:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">Forme</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">Code</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">Generator</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">2.0</span><span style="color:#C3E88D;">.0</span></span></code></pre></div><p>You can get some info on the available commands as follows:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">forme</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">list</span></span></code></pre></div><p>You&#39;re now ready to create new Forme projects!</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>You&#39;ll need to have already set up your local environment according to the instructions provided, or this probably won&#39;t work!</p></div><h2 id="bash-completion" tabindex="-1">Bash completion <a class="header-anchor" href="#bash-completion" aria-label="Permalink to &quot;Bash completion&quot;">​</a></h2><p>You can add the following line to your <code>bash_profile</code> to enable tab completion.</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">eval</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;$(</span><span style="color:#FFCB6B;">forme</span><span style="color:#C3E88D;"> completion</span><span style="color:#89DDFF;">)&quot;</span></span></code></pre></div>',14),t=[l];function p(c,i,r,d,h,u){return s(),e("div",null,t)}const C=a(n,[["render",p]]);export{m as __pageData,C as default};
